package gen.mt_research;
import gen.neo4jlib.neo4j_qry;
import java.io.File;
import java.io.FileWriter;
import org.neo4j.procedure.Description;
import org.neo4j.procedure.Name;
import org.neo4j.procedure.UserFunction;


public class seq_to_tiles_and_pattern {
   public static int tile_len = 100;
   public static int tile_partitions = 17000/tile_len;
   public static String[][] tile_seq = null;
   public static int tile_ct=tile_partitions;
   public static int tile_seq_ct[] = null; 
   public static int tid = 0;

    @UserFunction
    @Description("Template used in creating new functions.")

    public String get_tiles_and_pattern(
        @Name("fasta") 
            String fasta 
  )
   
         { 
             
        get_pattern(fasta);
         return "";
            }

    
    
    public static void main(String args[]) {
        get_pattern("");
    }
    
     public static String get_pattern(String fasta) 
    {
        gen.neo4jlib.neo4j_info.neo4j_var();
        gen.neo4jlib.neo4j_info.neo4j_var_reload();
        
        String fn = gen.neo4jlib.neo4j_info.Import_Dir + "new_seq.csvv";
        File f = new File(fn);
         FileWriter fw =null;
         try
        {
           fw= new FileWriter(f);
            fw.write("n|tile_id\n");
        }
        catch(Exception e) {}
        
        gen.neo4jlib.neo4j_info.neo4j_var();
        gen.neo4jlib.neo4j_info.neo4j_var_reload();
        String tile[] = new String[tile_partitions];
        //int i = 0;
        int nn = 0;
        for (int j=0; j<tile_partitions; j++)
           {
               nn = j + 1;
               try
               {
                  if (fasta.length() < (j+1)*tile_len) {  //shorter last partition
                   tile[j] = fasta.substring(j * tile_len, fasta.length());
                    tid = process_tile(nn, tile[j]);   
                  }
                  else
                  {  //full length partition
                   tile[j] = fasta.substring(j * tile_len, (j+1) * tile_len);
                   tid = process_tile(nn, tile[j]);   
                  }
                //ckit[i][j] = n;   

                fw.write(nn + "|" + tid + "\n");
                
               }
               catch(Exception e){ //triggered when last lines are void
                   //System.out.println("Error # 102\t" + j + "\t" + e.getMessage());
               }           
           }
        try
        {
        fw.flush();
        fw.close();
        }
        catch(Exception e){}
        
        return "";
    }
     
     public static int process_tile(int n, String seq)
     {
         String t = "";
         
         int tid=-999;
         String cq ="MATCH (t:tile) where t.n=" + n  + " and t.seq='" + seq + "' return t.tile_id as tid";
         try
         {
            tid = Integer.valueOf(gen.neo4jlib.neo4j_qry.qry_to_csv(cq).split("\n")[0]);
         }
         catch(Exception e)
         {
             tid = Integer.valueOf(gen.neo4jlib.neo4j_qry.qry_to_csv("MATCH (t:tile{n:" + n + "}) RETURN max(t.tile_id) + 1 as new_tid").split("\n")[0]);
             gen.neo4jlib.neo4j_qry.qry_write("create (t:tile{n:" + n + ",tile_id:" + tid + ",seq:'" + seq + "'})");
                      
         }
//         System.out.println(n+1 + "\t" + tid);
         return tid;
     }
     
}
